import{V as t}from"./chunks/theme.s689GAW8.js";import{c as i,J as a,p as s,V as r,o as n}from"./chunks/framework.KvD4Dfa9.js";const o=r('<h1 id="what-is-this-site-for" tabindex="-1">What is this site for? <a class="header-anchor" href="#what-is-this-site-for" aria-label="Permalink to &quot;What is this site for?&quot;">​</a></h1><p>Welcome to the Rocky Mountain ADA brand guidelines. This documentation offers the fundamental principles behind our brand and the necessary assets to implement it in your projects. Please consider these guidelines a living document that will evolve as we continue to develop the tenets and specifics of our brand design.</p><h2 id="our-brand" tabindex="-1">Our Brand <a class="header-anchor" href="#our-brand" aria-label="Permalink to &quot;Our Brand&quot;">​</a></h2><p>Our personality establishes the foundation of the Rocky Mountain ADA brand. It is a product of our mission, and a reflection of our culture, values, and promise to customers. We make sure that these traits come through in all of our communications and content.</p><ul><li>Helpful</li><li>Optimistic</li><li>Practical with a wink</li></ul><p>Our guidelines have space for flexibility, and are continuously revised and expanded to suit changing needs and up-to-date design trends and styling.</p><h2 id="accessible-design-system" tabindex="-1">Accessible Design System <a class="header-anchor" href="#accessible-design-system" aria-label="Permalink to &quot;Accessible Design System&quot;">​</a></h2><p>Accessibility guaranties the interaction of people with disabilities with our digital or physical materials.</p><p>This design system is built with all users in mind. It offers the same range of possibilities to users with different physical problems breaking barriers in terms of interaction</p><h3 id="further-reading-regulations-and-resources" tabindex="-1">Further Reading, Regulations, and Resources <a class="header-anchor" href="#further-reading-regulations-and-resources" aria-label="Permalink to &quot;Further Reading, Regulations, and Resources&quot;">​</a></h3><ul><li><a href="https://www.w3.org/WAI/intro/wcag" target="_blank" rel="noreferrer">Web Content Accessibility Guidelines (WCAG) from W3C</a></li><li><a href="https://www.section508.gov/section-508-of-the-rehabilitation-act" target="_blank" rel="noreferrer">Section 508 of the U.S. Rehabilitation Act</a></li><li><a href="https://a11yproject.com/" target="_blank" rel="noreferrer">The a11y Project</a></li></ul><h2 id="your-contact" tabindex="-1">Your Contact <a class="header-anchor" href="#your-contact" aria-label="Permalink to &quot;Your Contact&quot;">​</a></h2>',12),p=JSON.parse('{"title":"What is this site for?","description":"","frontmatter":{},"headers":[],"relativePath":"introduction.md","filePath":"introduction.md","lastUpdated":1707538225000}'),l={name:"introduction.md"},f=Object.assign(l,{setup(c){const e=[{avatar:"https://www.github.com/jessemutz.png",name:"Jesse Mutzebaugh",title:"Founder",org:"New Vista Digital",orgLink:"https://newvistadigital.com/",desc:'Reach Jesse at <a href="tel:7196391470">719-639-1470',links:[{icon:"github",link:"https://github.com/jessemutz"},{icon:"instagram",link:"https://instagram.com/newvistadigital"},{icon:"linkedin",link:"https://linkedin.com/in/jessemutz"},{icon:"twitter",link:"https://twitter.com/jessemutz"}]}];return(h,u)=>(n(),i("div",null,[o,a(s(t),{size:"medium",members:e})]))}});export{p as __pageData,f as default};
